package org.shvetsov;

import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Test;

import static org.assertj.core.api.Assertions.assertThat;

class _2370Test {

    private static _2370 task;
    private static _2370.TimeLimitExceeded taskTimeLimitExceeded;

    @BeforeEach
    public void setup() {
        task = new _2370();
        taskTimeLimitExceeded = new _2370.TimeLimitExceeded();
    }

    @Test
    void longestIdealString() {
        assertThat(task.longestIdealString("acfgbd", 2)).isEqualTo(4);
        assertThat(task.longestIdealString("abcd", 3)).isEqualTo(4);
    }

    @Test
    void longestIdealString1() {
        assertThat(task.longestIdealString("eduktdb", 15)).isEqualTo(5);
        assertThat(task.longestIdealString("pvjcci", 4)).isEqualTo(2);
        assertThat(task.longestIdealString("fqyokqgzvjpermqmwcjqtzbxnurjmawsswlwzmnjbbhdtfjxnktwtonpeorewc", 3)).isEqualTo(19);
        assertThat(task.longestIdealString("dyyonfvzsretqxucmavxegvlnnjubqnwrhwikmnnrpzdovjxqdsatwzpdjdsneyqvtvorlwbkb", 7)).isEqualTo(42);
        assertThat(task.longestIdealString("", 9)).isEqualTo(2681);
    }


    @Test
    void longestIdealStringTimeLimitExceeded() {
        assertThat(taskTimeLimitExceeded.longestIdealString("acfgbd", 2)).isEqualTo(4);
        assertThat(taskTimeLimitExceeded.longestIdealString("abcd", 3)).isEqualTo(4);
        assertThat(taskTimeLimitExceeded.longestIdealString("eduktdb", 15)).isEqualTo(5);
        assertThat(taskTimeLimitExceeded.longestIdealString("pvjcci", 4)).isEqualTo(2);
        assertThat(taskTimeLimitExceeded.longestIdealString("fqyokqgzvjpermqmwcjqtzbxnurjmawsswlwzmnjbbhdtfjxnktwtonpeorewc", 3)).isEqualTo(19);
        assertThat(taskTimeLimitExceeded.longestIdealString("dyyonfvzsretqxucmavxegvlnnjubqnwrhwikmnnrpzdovjxqdsatwzpdjdsneyqvtvorlwbkb", 7)).isEqualTo(42);
        assertThat(taskTimeLimitExceeded.longestIdealString("", 9)).isEqualTo(2681);
    }

}